	(2, 'Direct Product Order - Customer', 'order_mail', 'Thank you for order', '<p style="text-align: center;"><img data-filename="favicon.ico" style="width: 22px;" src="/uploads/email/16871580300.png"><br><p>Dear [customer_full_name],</p><p>Congratulations, Your Order has been successfully send!</p><p>Thank You</p><p>The [company_name] Team.</p><p></p></p>', 1, '2024-01-22 01:05:00', '2024-01-22 01:05:00'),
	(3, 'Password Reset', 'forgot_password', 'Reset Your Account Password', '<p style="text-align: center;"><img data-filename="favicon.ico" style="width: 22px;" src="/uploads/email/16871581060.png"><br><p>Dear [customer_full_name],</p><p>To reset your password click here</p><p>Thank You</p><p>The [company_name] Team.</p><p></p><p></p></p>', 1, '2024-01-22 01:05:00', '2024-01-22 01:05:00'),
	(4, 'Email Verification', 'verification_email', 'Account Verification Email', '<p style="text-align: center;"><img data-filename="favicon.ico" style="width: 22px;" src="/uploads/email/16871581550.png"><br><p>Dear [customer_full_name],</p><p>To verify your email please click here.</p><p>[verify_btn]</p><p>Thank You</p><p>The [company_name] Team.</p><p></p><p></p><p></p></p>', 1, '2024-01-22 01:05:00', '2024-01-22 01:05:00'),
	(5, 'Customer Registration', 'registration', 'Thank you for registration', '<p style="text-align: center;"><img data-filename="favicon.ico" style="width: 22px;" src="/uploads/email/16871581870.png"><br><p>Dear [customer_full_name],</p><p>Congratulations, Your Account has been successfully created!</p><p>Thank You</p><p>The [company_name] Team.</p><p></p></p>', 1, '2024-01-22 01:05:00', '2024-01-22 01:05:00'),
	(6, 'Withdraw Approved - Merchant', 'withdraw', 'Withdraw success', '<p style="text-align:center;"><img data-filename="favicon.ico" style="width: 22px;" src="/uploads/email/16871757290.png"><br><p>Dear [customer_full_name],</p><p>Congratulations, Your Withdraw request has been successfully approved! Your payment will be sent to your payment account.&nbsp;</p><p>Please Check</p><p>Thank You</p><p>The [company_name] Team.</p><p><br></p><p></p><p></p><p></p><p></p><p></p><p></p><p></p><p></p></p>', 1, '2024-01-22 01:05:00', '2024-01-22 01:05:00'),
	(7, 'Bidding Winner - Customer', 'bid_winner', 'Thank you for Bid Winning', '<p style="text-align: center;"><img data-filename="favicon.ico" style="width: 22px;" src="/uploads/email/16871580300.png"><br><p>Dear [customer_full_name],</p><p>Congratulations!! You won the bid please confirm your final payment.</p><p>Thank You</p><p>The [company_name] Team.</p><p></p><p></p></p>', 1, '2024-01-22 01:05:00', '2024-01-22 01:05:00'),
	(8, 'Bidding Final Payment - Customer', 'final_payment', 'Thank you for final payment', '<p style="text-align: center;"><img data-filename="favicon.ico" style="width: 22px;" src="/uploads/email/16871580300.png"><br><p>Dear [customer_full_name],</p><p>Congratulations!! Your payment is successful we will shift your product and deliver as soon as possible.</p><p>Thank You</p><p>The [company_name] Team.</p><p></p><p></p></p>', 1, '2024-01-22 01:05:00', '2024-01-22 01:05:00'),
	(9, 'Password Reset Confirmation', 'password_reset_confirmation', 'Thank you for Password Reset', '<p style="text-align: center;"><img data-filename="favicon.ico" style="width: 22px;" src="/uploads/email/16871580300.png"><br><p>Dear [customer_full_name],</p><p>Your password has been successfully reset. Now you can login in your account.</p><p>Thank You</p><p>The [company_name] Team.</p><p></p><p></p></p>', 1, '2024-01-22 01:05:00', '2024-01-22 01:05:00'),
	(10, 'Merchant Registration', 'merchant_registration', 'Thank you for Registration', '<p style="text-align: center;"><img data-filename="favicon.ico" style="width: 22px;" src="/uploads/email/16871580300.png"><br><p>Dear [customer_full_name],</p><p>Congratulations!! Your merchant registration is successful now you can list your product and sell to the customer.</p><p>Thank You</p><p>The [company_name] Team.</p><p></p><p></p><p></p></p>', 1, '2024-01-22 01:05:00', '2024-01-22 01:05:00'),
	(11, 'Bidding - Customer', 'bidding_customer', 'Thank you for Bidding', '<p style="text-align: center;"><img data-filename="favicon.ico" style="width: 22px;" src="/uploads/email/16871580300.png"><br><p>Dear [customer_full_name],</p><p>Congratulations!! Your bidding is successfully placed. Stay connected for the updates.</p><p>Thank You</p><p>The [company_name] Team.</p><p></p><p></p></p>', 1, '2024-01-22 01:05:00', '2024-01-22 01:05:00'),
	(12, 'Bidding Payment Refund - Customer', 'bidding_payment_refund', 'Thank you for Bidding & Bidding Payment Refund', '<p style="text-align: center;"><img data-filename="favicon.ico" style="width: 22px;" src="/uploads/email/16871580300.png"><br><p>Dear [customer_full_name],</p><p>Your partial bidding amount has been successfully refunded to your account wallet.&nbsp;</p><p>Thank You</p><p>The [company_name] Team.</p><p></p><p></p></p>', 1, '2024-01-22 01:05:00', '2024-01-22 01:05:00'),
	(13, 'Withdraw Rejected - Merchant', 'Withdraw Rejected - Merchant', 'Your Withdraw Rejected', '<p style="text-align: center;"><img data-filename="favicon.ico" style="width: 22px;" src="/uploads/email/16871580300.png"><br><p>Dear [customer_full_name],</p><p>Your withdrawal request has been rejected. Please contact the admin for the update.</p><p>Thank You</p><p>The [company_name] Team.</p><p></p><p></p></p>', 1, '2024-01-22 01:05:00', '2024-01-22 01:05:00'),
	(14, 'Withdraw Requested - Admin', 'withdraw_requested', 'Thank you for Withdraw Requested', '<p style="text-align: center;"><img data-filename="favicon.ico" style="width: 22px;" src="/uploads/email/16871580300.png"><br><p>Dear [customer_full_name],</p><p>Your withdrawal request has been placed. Will update you regarding this soon. Stay connected&nbsp;</p><p>Thank You</p><p>The [company_name] Team.</p><p></p><p></p></p>', 1, '2024-01-22 01:05:00', '2024-01-22 01:05:00'),
	(15, 'Support Ticket Reply - Merchant', 'support_ticket_reply', 'Thank you for the Support Ticket Reply', '<p style="text-align: center;"><img data-filename="favicon.ico" style="width: 22px;" src="/uploads/email/16871580300.png"><br><p>Dear [customer_full_name],</p><p>Your support ticket has been successfully submitted. Soon support member will response to your support.&nbsp;</p><p>Thank You</p><p>The [company_name] Team.</p><p></p><p></p></p>', 1, '2024-01-22 01:05:00', '2024-01-22 01:05:00'),
	(16, 'Support Ticket - Admin', 'support_ticket', 'Thank you for the Support Ticket', '<p style="text-align: center;"><img data-filename="favicon.ico" style="width: 22px;" src="/uploads/email/16871580300.png"><br><p>Dear [customer_full_name],</p><p>Your support ticket has been successfully submitted. Soon support member will response to your support.&nbsp;<br></p><p>Thank You</p><p>The [company_name] Team.</p><p></p><p></p></p>', 1, '2024-01-22 01:05:00', '2024-01-22 01:05:00');
